import React, { useState } from "react";
import '../index.css';
import Grid from "./Grid";
import Screen from "./Screen";

function Calculator()
{
    const [screenText,upDateScreenText]=useState("");

    function handelScreenUpdate(value)
    {
        upDateScreenText(screenText.concat(value));
    }
    return(<>
    <Screen textValue={screenText}/>
    <div className='grid'>
    <button >0</button>
    <button>1</button>
    <button>2</button>
    <button>3</button>
    <button>4</button>
    <button>5</button>
    <button>6</button>
    <button>7</button>
    <button>8</button>
    <button>9</button>
    <button>.</button>
    <button>%</button>
    <button>+</button>
    <button>*</button>
    <button>-</button>
    <button>/</button>
    <button>clr</button>
    <button>Ans</button>
    </div>
    </>
   );
}

export default Calculator;
